import React from 'react';
import PopupWithForm from './PopupWithForm.js';

function AddPlacePopup({ isOpen, isAddPlacePopupOpen, closeAllPopups, onAddPlace }) {
    const cardnameRef = React.useRef('');
    const cardlinkRef = React.useRef('');

    function handleSubmit(evt) {
        evt.preventDefault();

        onAddPlace({
            name: cardnameRef.current.value,
            link: cardlinkRef.current.value
        });
    };

    React.useEffect(() => {
        cardnameRef.current.value = '';
        cardlinkRef.current.value = '';
    }, [isOpen]);

    return (
        /*<section className={`popup popup-add ${isOpen ? "popup_opened" : ""}`}>
            <div className="popup__container popup__container-add">
                <form handleSubmit={handleSubmit} name="add" className="popup__form popup__form-add" noValidate>
                    <h3 className="popup__title popup__title-add">Новое место</h3>
                    <input
                        ref={cardnameRef}
                        id="cardname"
                        type="text"
                        name="cardname"
                        placeholder="Название"
                        className="popup__input popup__input_type_cardname"
                        minLength="2"
                        maxLength="30"
                        required
                    />
                    <span className="cardname-error popup__form-error"></span>
                    <input
                        ref={cardlinkRef}
                        id="cardlink"
                        type="url"
                        name="cardlink"
                        placeholder="Ссылка на картинку"
                        className="popup__input popup__input_type_cardlink"
                        required
                    />
                    <span className="cardlink-error popup__form-error"></span>
                    <button type="submit" className="popup__save-button">Создать</button>
                </form>
                <button onClick={onClose} type="button" className="popup__close-button"></button>
            </div>
        </section>*/
        <PopupWithForm
            isOpen={isAddPlacePopupOpen}
            onClose={closeAllPopups}
            handleSubmit={handleSubmit}
            name="add"
            title="Новое место"
            buttonText="Создать"
        >
            <input
            ref={cardnameRef}
            id="cardname"
            type="text"
            name="cardname"
            placeholder="Название"
            className="popup__input popup__input_type_cardname"
            minLength="2"
            maxLength="30"
            required
            />
            <span className="cardname-error popup__form-error"></span>
            <input
            ref={cardlinkRef}
            id="cardlink"
            type="url"
            name="cardlink"
            placeholder="Ссылка на картинку"
            className="popup__input popup__input_type_cardlink"
            required
            />
            <span className="cardlink-error popup__form-error"></span>
        </PopupWithForm>
    );
}

export default AddPlacePopup;
