import React from 'react';

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar }) {
    const avatarRef = React.useRef('');

    function handleSubmit(evt) {
        evt.preventDefault();

        onUpdateAvatar({
            avatar: avatarRef.current.value
        })
    };

    React.useEffect(() => {
        avatarRef.current.value = ''
    }, [isOpen]);

    return (
        <section className={`popup popup-avatar ${isOpen ? "popup_opened" : ""}`}>
            <div className="popup__container popup__container-avatar">
                <form onSubmit={handleSubmit} name="avatar" className="popup__form popup__form-avatar" noValidate>
                    <h3 className="popup__title popup__title-avatar">Обновить аватар</h3>
                    <input
                        ref={avatarRef}
                        id="avatarlink"
                        type="url"
                        name="avatarlink"
                        placeholder="Ссылка на аватар"
                        className="popup__input popup__input_type_avatarlink"
                        required
                    />
                    <span className="avatarlink-error popup__form-error"></span>
                    <button type="submit" className="popup__save-button">Сохранить</button>
                </form>
                <button onClick={onClose} type="button" className="popup__close-button"></button>
            </div>
        </section>
    );
}

export default EditAvatarPopup;
