import useFormValidator from "../utils/useFormValidator";
import { validateForm } from "../utils/validateForm";

function PopupWithForm({ name, title, isOpen, isLoading, onClose, buttonText, children, handleSubmit, buttonLoadingText }) {
    const { isValid } = useFormValidator(validateForm);
    return (
        <section className={`popup popup-${name} ${isOpen ? "popup_opened" : ""}`}>
            <div className={`popup__container popup__container-${name}`}>
                <form onSubmit={handleSubmit} name={`${name}`} className={`popup__form popup__form-${name}`} noValidate>
                    <h3 className={`popup__title popup__title-${name}`}>{`${title}`}</h3>
                    {children}
                    <button type="submit" className="popup__save-button">{`${isLoading ? buttonLoadingText : buttonText}`}</button>
                </form>
                <button disabled={!isValid} onClick={onClose} type="button" className="popup__close-button"></button>
            </div>
        </section>
    );
}

export default PopupWithForm;
