import React from 'react';

function useFormValidator () {
    const [formValues, setFormValues] = React.useState({});
    const [formValidity, setFormValidity] = React.useState(false);

    const handleInputChange = React.useCallback((evt) => {
        const  { name, value } = evt.target;
    
        setFormValues(prevState => ({...prevState, [name]: value}));
    }, [setFormValues]);

    function validateInputs() {
        const isInputsFilled = formValues.length > 0;
        const isInputsValid = isInputsFilled;

        setFormValidity(prevValidity => ({
            ...prevValidity, isInputsValid
        }));

    }
    
    React.useEffect(() => {
        validateInputs();
    }, [formValues, setFormValidity]);

    return { handleInputChange, validateInputs };
}

export default useFormValidator;
