import React from 'react';

function useFormValidator () {
    const [formValues, setFormValues] = React.useState({});
    const [formErrors, setFormErrors] = React.useState({});
    const [isValid, setIsValid] = React.useState(true);

    const handleInputChange = React.useCallback((evt) => {
        const  { name, value } = evt.target;

        setFormValues({...formValues, [name]: value});
        setFormErrors({...formErrors, [name]: evt.target.validationMessage});
        setIsValid(evt.target.closest('form').checkValidity());
    });

    const resetForm = React.useCallback((
        newFormValues = {},
        newFormErrors = {},
        newIsValid = false
        ) => {
            setFormValues(newFormValues);
            setFormErrors(newFormErrors);
            setIsValid(newIsValid);
    }, [setFormValues, setFormErrors, setIsValid]);

    return { formValues, formErrors, isValid, handleInputChange, resetForm, setFormValues, setIsValid };
}

export default useFormValidator;
