import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import useFormValidator from '../utils/useFormValidator.js';
import { validateForm } from '../utils/validateForm.js';

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar, isLoading }) {
    const avatarRef = React.useRef('');
    const { formValues, formErrors, isValid, handleInputChange, resetForm } = useFormValidator(validateForm); 

    function handleSubmit(evt) {
        evt.preventDefault();

        onUpdateAvatar({
            avatar: avatarRef.current.value
        });
    };

    React.useEffect(() => {
        if (!isOpen) {
            resetForm();
        }

        avatarRef.current.value = null;
    }, [isOpen, resetForm]);

    return (
        <PopupWithForm
            isOpen={isOpen}
            onClose={onClose}
            handleSubmit={handleSubmit}
            isLoading={isLoading}
            isFormValid={isValid}
            name="avatar"
            title="Обновить аватар"
            buttonText="Сохранить"
            buttonLoadingText="Сохранение..."
        >
            <input
                ref={avatarRef}
                id="avatarlink"
                type="url"
                name="avatarlink"
                onChange={handleInputChange}
                placeholder="Ссылка на аватар"
                className="popup__input popup__input_type_avatarlink"
                required
            />
            {!isValid && formValues.avatarlink && <div className="avatarlink-error popup__form-error">{formErrors['avatar'].avatarlink}</div>}
        </PopupWithForm>
    );
}

export default EditAvatarPopup;
