import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import useFormValidator from '../utils/useFormValidator.js';

function AddPlacePopup({ isOpen, onClose, onAddPlace, isLoading }) {
    const cardnameRef = React.useRef('');
    const cardlinkRef = React.useRef('');


    const { handleInputChange } = useFormValidator();

    function handleSubmit(evt) {
        evt.preventDefault();

        onAddPlace({
            name: cardnameRef.current.value,
            link: cardlinkRef.current.value
        });
    };

    React.useEffect(() => {
        cardnameRef.current.value = '';
        cardlinkRef.current.value = '';
    }, [isOpen]);

    return (
        <PopupWithForm
            isOpen={isOpen}
            onClose={onClose}
            handleSubmit={handleSubmit}
            isLoading={isLoading}
            buttonLoadingText="Сохранение..."
            name="add"
            title="Новое место"
            buttonText="Создать"
        >
            <input
                ref={cardnameRef}
                id="cardname"
                type="text"
                name="cardname"
                onChange={handleInputChange}
                placeholder="Название"
                className="popup__input popup__input_type_cardname"
                minLength="2"
                maxLength="30"
                required
            />
            <span className="cardname-error popup__form-error"></span>
            <input
                ref={cardlinkRef}
                id="cardlink"
                type="url"
                name="cardlink"
                onChange={handleInputChange}
                placeholder="Ссылка на картинку"
                className="popup__input popup__input_type_cardlink"
                required
            />
            { <span className="cardlink-error popup__form-error"></span>}
        </PopupWithForm>
    );
}

export default AddPlacePopup;
