import React from 'react';
import Header from './Header.js';
import Main from './Main.js';
import Footer from './Footer.js';
import PopupWithForm from './PopupWithForm.js';
import PopupWithImage from './PopupWithImage.js';
import api from '../utils/api.js';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = React.useState(false);
  const [isAddPlacePopupOpen, setIsisAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = React.useState(false);
  const [userName, setUserName] = React.useState();
  const [userDescription, setUserDescription] = React.useState();
  const [userAvatar, setUserAvatar] = React.useState();
  const [cards, setCards] = React.useState([]);

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  };

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsisAddPlacePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditAvatarPopupOpen(false);
    setIsEditProfilePopupOpen(false);
    setIsisAddPlacePopupOpen(false);
  }

  React.useEffect(() => {
    api.getUserInfo()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((err) => console.log(err));
  }, []);

  /*React.useEffect(() => {
    api.getUserInfo()
      .then((data) => {
        setUserDescription(data.about);
      })
      .catch((err) => console.log(err));
  }, [userDescription]);

  React.useEffect(() => {
    api.getUserInfo()
      .then((data) => {
        setUserAvatar(data.avatar);
      })
      .catch((err) => console.log(err));
  }, [userAvatar]);*/

  React.useEffect(() => {
    api.getInitialCards()
      .then((cards) => {
        setCards(cards);
      })
      .catch((err) => console.log(err));
  }, [])

  return (
    <div>
      <Header />
      <Main name={userName}
        about={userDescription}
        avatar={userAvatar}
        onEditAvatar={handleEditAvatarClick}
        onAddPlace={handleAddPlaceClick}
        onEditProfile={handleEditProfileClick}/>
      <section className="cards">
        {cards.map((card) => (
          <div className="card__container" key={card._id}>
            <button type="button" className="card__delete-button"></button>
            <img 
              style={{ backgroundImage: `url(${card.link})`}}
              className="card__image"
              alt=""
              src=""
            />
            <div className="card__group">
              <h2 className="card__title">{card.name}</h2>
              <div className="card__like-group">
                <button type="button" className="card__like-button"></button>
                <span className="card__likes-number">{card.likes.length}</span>
              </div>
            </div>
          </div>
        ))}
      </section>
      <Footer />
      <PopupWithForm isOpen={isEditProfilePopupOpen} onClose={closeAllPopups} name="edit" title="Редактировать профиль" buttonText="Сохранить">
        <input id="username" type="text" name="username" placeholder="Имя" className="popup__input popup__input_type_username" minLength="2" maxLength="40" required/>
        <span className="username-error popup__form-error"></span>
        <input id="about" type="text" name="about" placeholder="Вид деятельности" className="popup__input popup__input_type_about" minLength="2" maxLength="200" required/>
        <span className="about-error popup__form-error"></span>
      </PopupWithForm>
      <PopupWithForm isOpen={isAddPlacePopupOpen} onClose={closeAllPopups} name="add" title="Новое место" buttonText="Создать">
        <input id="cardname" type="text" name="cardname" placeholder="Название" className="popup__input popup__input_type_cardname" minLength="2" maxLength="30" required/>
        <span className="cardname-error popup__form-error"></span>
        <input id="cardlink" type="url" name="cardlink" placeholder="Ссылка на картинку" className="popup__input popup__input_type_cardlink" required/>
        <span className="cardlink-error popup__form-error"></span>
      </PopupWithForm>
      <PopupWithForm isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups} name="avatar" title="Обновить аватар" buttonText="Сохранить">
        <input id="avatarlink" type="url" name="avatarlink" placeholder="Ссылка на аватар" className="popup__input popup__input_type_avatarlink" required/>
        <span className="avatarlink-error popup__form-error"></span>
      </PopupWithForm>
      <PopupWithForm name="confirm" title="Вы уверены?" buttonText="Да"></PopupWithForm>
      <PopupWithImage />
    </div>  
  );
}

export default App;
