export function validateForm (values, setErrors, setIsValid) {
    const errors = {};
    let valid = true;

    if (!values.cardname) {
        errors.cardname = 'Введите название';
        valid = false;
    } else if (values.cardname.length < 2) {
        errors.cardname = 'Минимальная длина названия - 2 символа';
        valid = false;
    } else if (values.cardname.length < 30) {
        errors.cardname = 'Максимальная длина названия - 30 символов';
        valid = false;
    }

    if (!values.cardlink) {
        errors.cardlink = 'Введите ссылку на картинку';
        valid = false;
    } else if (!/^(ftp|http|https):\/\/[^ "]+$/.test(values.cardlink)) {
        errors.cardlink = 'Введите корректную ссылку на картинку';
        valid = false;
    }

    setErrors(errors);
    setIsValid(valid);
};
