function PopupWithForm(props) {
    return (
        <section className={`popup popup-${props.name} ${props.isOpen ? "popup_opened" : ""}`}>
            <div className={`popup__container popup__container-${props.name}`}>
                <form name={`${props.name}`} className={`popup__form popup__form-${props.name}`} noValidate>
                    <h3 className={`popup__title popup__title-${props.name}`}>{`${props.title}`}</h3>
                    {props.children}
                    <button type="submit" className="popup__save-button">{`${props.buttonText}`}</button>
                </form>
                <button onClick={props.onClose} type="button" className="popup__close-button"></button>
            </div>
        </section>
    );
}

export default PopupWithForm;
