export function validateForm (values, setErrors, setIsValid, componentName) {
    const errors = {};
    let isValid = true;

    if (componentName === 'AddPlacePopup') {
        if (!values.cardname) {
            errors.cardname = 'Введите название';
            isValid = false;
        } else if (values.cardname.length < 2) {
            errors.cardname = 'Минимальная длина названия - 2 символа';
            isValid = false;
        } else if (values.cardname.length > 30) {
            errors.cardname = 'Максимальная длина названия - 30 символов';
            isValid = false;
        }

        if (!values.cardlink) {
            errors.cardlink = 'Введите ссылку на картинку';
            isValid = false;
        } else if (!/^(ftp|http|https):\/\/[^ "]+$/.test(values.cardlink)) {
            errors.cardlink = 'Введите корректную ссылку на картинку';
            isValid = false;
        }
    }

    if (componentName === 'EditAvatarPopup') {
        if (!values.avatarlink) {
            errors.avatarlink = 'Введите ссылку на аватар';
            isValid = false;
        } else if (!/^(ftp|http|https):\/\/[^ "]+$/.test(values.avatarlink)) {
            errors.avatarlink = 'Введите корректную ссылку на аватар';
            isValid = false;
        }
    }

    setErrors(errors);
    setIsValid(isValid);
};
