import React from 'react';
import CurrentUserContext from '../contexts/CurrentUserContext.js';
import PopupWithForm from './PopupWithForm.js';

function EditProfilePopup({ isOpen, onUpdateUser }) {
    const [name, setName] = React.useState('');
    const [description, setDescription] = React.useState('');
    const currentUser = React.useContext(CurrentUserContext);

    React.useEffect(() => {
        setName(currentUser.name);
        setDescription(currentUser.about);
    }, [currentUser, isOpen]);

    function handleNameChange(evt) {
        setName(evt.target.value);
    };

    function handleDescriptionChange(evt) {
        setDescription(evt.target.value);
    };

    function handleSubmit(evt) {
        evt.preventDefault();
      
        onUpdateUser({
          name,
          about: description,
        });
    };

    return (
        /*<section className={`popup popup-edit ${isOpen ? "popup_opened" : ""}`}>
            <div className="popup__container popup__container-edit">
                <form onSubmit={handleSubmit} name="name" className="popup__form popup__form-edit" noValidate>
                    <h3 className="popup__title popup__title-edit">Редактировать профиль</h3>
                    <input
                        onChange={handleNameChange}
                        value={name || ''}
                        id="username"
                        type="text"
                        name="username"
                        placeholder="Имя"
                        className="popup__input popup__input_type_username"
                        minLength="2"
                        maxLength="40"
                        required
                    />
                    <span className="username-error popup__form-error"></span>
                    <input
                        onChange={handleDescriptionChange}
                        value={description || ''}
                        id="about"
                        type="text"
                        name="about"
                        placeholder="Вид деятельности"
                        className="popup__input popup__input_type_about"
                        minLength="2"
                        maxLength="200"
                        required
                    />
                    <span className="about-error popup__form-error"></span>
                    <button type="submit" className="popup__save-button">Сохранить</button>
                </form>
                <button onClick={onClose} type="button" className="popup__close-button"></button>
            </div>
        </section>*/
        <PopupWithForm
            isOpen={isOpen}
            onClose={closeAllPopups}
            handleSubmit={handleSubmit}
            name="edit"
            title="Редактировать профиль"
            buttonText="Сохранить"
        >
            <input
                onChange={handleNameChange}
                value={name || ''}
                id="username"
                type="text"
                name="username"
                placeholder="Имя"
                className="popup__input popup__input_type_username"
                minLength="2"
                maxLength="40"
                required
            />
            <span className="username-error popup__form-error"></span>
            <input
                onChange={handleDescriptionChange}
                value={description || ''}
                id="about"
                type="text"
                name="about"
                placeholder="Вид деятельности"
                className="popup__input popup__input_type_about"
                minLength="2"
                maxLength="200"
                required
            />
            <span className="about-error popup__form-error"></span>
        </PopupWithForm>
    );
}

export default EditProfilePopup;
