import React from 'react';
import PopupWithForm from './PopupWithForm.js';
import useFormValidator from '../utils/useFormValidator.js';

function AddPlacePopup({ isOpen, onClose, onAddPlace, isLoading }) {
    const cardnameRef = React.useRef('');
    const cardlinkRef = React.useRef('');


    const validate = React.useCallback((values, setErrors, setIsValid) => {
        const errors = {};
        let isValid = true;

        if (!values.cardname) {
            errors.cardname = 'Введите название';
            isValid = false;
        } else if (values.cardname.lenght < 2) {
            errors.cardname = 'Минимальная длина названия - 2 символа';
            isValid = false;
        } else if (values.cardname.lenght < 30) {
            errors.cardname = 'Максимальная длина названия - 30 символов';
            isValid = false;
        }

        if (!values.cardlink) {
            errors.cardlink = 'Введите ссылку на картинку';
            isValid = false;
        } else if (!/^(ftp|http|https):\/\/[^ "]+$/.test(values.cardlink)) {
            errors.cardlink = 'Введите корректную ссылку на картинку';
            isValid = false;
        }

        setErrors(errors);
        setIsValid(isValid);
    }, []);

    const { formValues, formErrors, isValid, handleInputChange, resetForm } = useFormValidator(validate); 

    function handleSubmit(evt) {
        evt.preventDefault();

        onAddPlace({
            name: cardnameRef.current.value,
            link: cardlinkRef.current.value
        });
    };

    React.useEffect(() => {
        cardnameRef.current.value = '';
        cardlinkRef.current.value = '';
    }, [isOpen]);

    return (
        <PopupWithForm
            isOpen={isOpen}
            onClose={onClose}
            handleSubmit={handleSubmit}
            isLoading={isLoading}
            buttonLoadingText="Сохранение..."
            name="add"
            title="Новое место"
            buttonText="Создать"
        >
            <input
                ref={cardnameRef}
                id="cardname"
                type="text"
                name="cardname"
                onChange={handleInputChange}
                placeholder="Название"
                className="popup__input popup__input_type_cardname"
                minLength="2"
                maxLength="30"
                required
            />
            <span className="cardname-error popup__form-error">{formErrors.cardname}</span>
            <input
                ref={cardlinkRef}
                id="cardlink"
                type="url"
                name="cardlink"
                onChange={handleInputChange}
                placeholder="Ссылка на картинку"
                className="popup__input popup__input_type_cardlink"
                required
            />
            <span className="cardlink-error popup__form-error">{formErrors.cardlink}</span>
        </PopupWithForm>
    );
}

export default AddPlacePopup;
